mapper.select:
select

from briefBill
where accountNumber=  #{accountnumber,jdbcType=VARCHAR}
and billType= #{billType,jdbcType=VARCHAR}
order by briefBillDate

mapper.insert:
insert into briefBill (briefBillId, billType, totalBill, 
  accountNumber, briefBillDate
  )
values (#{briefbillid,jdbcType=INTEGER}, #{billtype,jdbcType=VARCHAR}, #{totalbill,jdbcType=INTEGER}, 
  #{accountnumber,jdbcType=VARCHAR}, #{briefbilldate,jdbcType=TIMESTAMP}
  )

mapper.update:
update briefBill
set billType = #{billtype,jdbcType=VARCHAR},
  totalBill = #{totalbill,jdbcType=INTEGER},
  accountNumber = #{accountnumber,jdbcType=VARCHAR},
  briefBillDate = #{briefbilldate,jdbcType=TIMESTAMP}
where briefBillId = #{briefbillid,jdbcType=INTEGER}
and  accountNumber = #{accountnumber,jdbcType=VARCHAR};
